import '../App.css';
import React from "react"
import {useState} from "react"
import {getTextObj} from '../JS/properties.js'
import {getConnection} from '../JS/connection.js'


function SEARCHSTRING(props) {

    const [btnClass, setbtnClass] = useState(['btnSearch', 'btn-secondary'])
    const [btnDisabled, setDisabled] = useState(true)
    const [input, setInput] = useState('')
    const [data,setData] = useState('')

    function btnSearchChange(val) {
        const findObj = getTextObj(val)
        if (!findObj.isNullorEmpty) {
            setbtnClass(['btnSearch', 'btn-secondary']);
            setDisabled(false);

        } else {
            setDisabled(true);
            setbtnClass(['btnSearch', 'btn-dark']);
        }
    }


    async function arm_Search (request) {


    return await getConnection(request)
            .then(response => {
            setData(JSON.stringify(response.suggestions, null, 4))})

}

return (
        <div className="searchtools">
            <div className="search_div">

                <label htmlFor="armsearch" className='searchlbl'>поиск</label>
                <input type="text" id="armsearch"
                       value={input}
                       onChange={e => {
                           setInput(e.target.value);
                           let x= e.target.value;

                           btnSearchChange(x);
                           arm_Search( x);
                           getConnection(x)
                           props.expData(x);
                       }
                       }

                       placeholder="Введите данные по компании"/>
                <button className={btnClass.join(' ')} disabled={btnDisabled}>Найти</button>


            </div>

            <div className="sug">
                <pre style={{ color: 'white', margin:'50px'}}> {data} </pre>

            </div>

        </div>
    )
}

export default SEARCHSTRING;
