import '../App.css';
import React,{useState} from "react"
import {getConnection} from "../JS/connection";
import {getTextObj} from '../JS/properties.js'



function SEARCHSTRING(props) {

    const [btnClass, setbtnClass] = useState(['btnSearch', 'btn-secondary'])
    const [btnDisabled, setDisabled] = useState(true)
    const [input, setInput] = useState('')
    const [data,setData]=useState('');


    function btnSearchChange(val) {
        const findObj = getTextObj(val)
        if (!findObj.isNullorEmpty) {
            setbtnClass(['btnSearch', 'btn-secondary']);
            setDisabled(false);

        } else {
            setDisabled(true);
            setbtnClass(['btnSearch', 'btn-dark']);
        }
    }


    async function arm_Search (request) {

        return  getConnection(request)
            .then(response => {
             JSON.stringify(response.suggestions, null, 4); return response})
            .then(response => {setData(response.suggestions);return response;} )


}

return (
        <div className="searchtools">
            <div className="search_div">

                <label htmlFor="armsearch" className='searchlbl'>поиск</label>
                <input type="text" id="armsearch"
                       value={input}
                       onChange={e => {
                           setInput(e.target.value);
                           const str= e.target.value;
                           btnSearchChange(str);
                           arm_Search(str);
                       }
                       }
                       placeholder="Введите данные по компании"/>
                <button className={btnClass.join(' ')} disabled={btnDisabled}>Найти</button>
            </div>
            <Sug data={ data} req = {input}/>
        </div>
    )
}

function Sug (props){
  //  <pre style={{color: 'white', margin: '50px'}}> {props.data} </pre>







        return (
            <div className="sug">
                {
                    props.data.map(el =>  {

                      return  <div className="suggs">
                            {el.data}
                        </div>
                    })
                }
            </div>

        )


}

    export default SEARCHSTRING;
