import React, { useState } from 'react';
import '../App.css';
import { getConnection } from '../JS/connection';
import GETCARDS from './GETCARDS';
import {getResponse} from '../JS/properties'

function SEARCHSTRING(props) {
//    const [state,setState]=useState('')
   const [inputStyle,setinputStyle]=useState([]);
   const [cardstate,setCardstate] = useState(0)



const setSuggestions=(e)=>{
    props.objState.update(getResponse(e).suggestions)

}
    
  
return (
       <div className="container">
            <div className={"lbl lead "}  >ПОИСК</div>

                <input type="text" name="searchstr" style = {{...inputStyle}}
                placeholder={'Введите поисковый запрос'} 
                onChange={ (e)=> {
                    if(e.target.value.length>0) {
            setinputStyle( 
                {textTransform:'uppercase'
                ,fontWeight:'500'
                ,letterSpacing: '1px'})}
                else {setinputStyle()} 
                  ;
        setCardstate(0);
        setSuggestions(e)
                }
            
            }
                />
<GETCARDS 
state = {props.objState.state} 
setCardstate = {props.setCardstate} 
cardstate={cardstate}/> 
  </div>

  
 
)
        }

    export default SEARCHSTRING;
