import '../App.css';
import React from "react"
import { useState } from "react"
import { getTextObj, getMassChangeClass} from '../JS/properties.js'



function SEARCHSTRING() {
  // const [search,setSearch] = useState(0)

  const [btnClass,setbtnClass] = useState( 
      ['btnSearch', 'btn-secondary','btn_disabled']
  
  )
  

  const [input, setInput] = useState('')
  function inputSet (value) {
    setInput(value)
  }
  
  function btnSearchChange() {
     const findObj= getTextObj(input) 
     console.log(input)
 
   const mass = getMassChangeClass(btnClass,'btn_disabled',findObj)
    setbtnClass(mass)
   

  
  }
  function arm_Search() {

    console.log ('Нажали поиск')
  }

  return (
    <div className="search_div">

      <label htmlFor="armsearch" className='searchlbl'>поиск</label>
      <input type="text" id="armsearch"
        value={input}
        onInput={inputSet( e=>  e.target.value)}
        onChange={btnSearchChange}

        placeholder="Введите данные по компании" />
      <button className={btnClass.join(' ')} onClick={arm_Search}>Найти</button>

    </div>
  )
}

export default SEARCHSTRING;
