import './App.css';
import HEAD from './COMPONENTS/HEAD';
import SEARCHSTRING from "./COMPONENTS/SEARCHSTRING";
import CARD from "./COMPONENTS/CARD"
import {useState} from 'react'
import CDI_CARD from './COMPONENTS/CDI_DATA';



function App() {
  const [state,setState]=useState('')
  const [cardstate,setCardstate] = useState(0)
  const [xx,setx] = useState('')

  let objState = {
    update : function (state) {
    setState(state)
  },
  state: state,
  setCardstate: function (x){setCardstate(x)}
 , cardstate:cardstate
}
 async function x() {
  const response = await 
  fetch('http://localhost:5000/post',
  {
  method: "POST",
  headers: { "Content-Type": "application/json"},
  body:`{
    "sql_type": "SELECT",
    "fields": ["*"],
    "sheme": "extrep_mdm",
    "table": "extendedreport",
    "dopSql": "limit 5"
  }`
}
  )
 const jsonData= await response.json()
 setx(JSON.stringify(jsonData,null,4))

}
x()

  return (
    <div className="App bg-dark border-danger h6 mr-5">
      <div className={'all'}>
        <div className={'fix'}>

          <HEAD/>

          <SEARCHSTRING objState = {objState}/>
        </div>
        <div className={"cards"}>
          <CARD 
          state ={objState.state}
          update = {objState.update}
          setCardstate = {objState.setCardstate}
          cardstate={objState.cardstate}
          />

          </div>
          {state && cardstate>1 ? <CDI_CARD objState = {state} /> :''}

          </div>

        <pre> {xx}</pre>

      </div>
  );



}


export default App;
