import '../App.css';
import React from "react"
import { useState } from "react"
import { getTextObj} from '../JS/properties.js'
import {getConnection} from '../JS/connection'



function SEARCHSTRING() {
  // const [search,setSearch] = useState(0)

  const [btnClass,setbtnClass] = useState( ['btnSearch', 'btn-secondary'])
  const [data,setData] = useState('')
  const [btnDisabled,setDisabled] = useState(true)
  const [input, setInput] = useState('')

  
  function btnSearchChange(val) {
  const findObj= getTextObj(val) 
  if(!findObj.isNullorEmpty)
   {
     setbtnClass(['btnSearch','btn-secondary']);
     setDisabled(false);
  
  } else {
    setDisabled(true);
    setbtnClass(['btnSearch','btn-dark']);
}
  }

  function arm_Search() {
    getConnection(input).then(resp=> {
     setData(JSON.stringify(resp.suggestions,null,4))
     ;
     return resp;
   })}


  return (
    <div className="mass">
    <div className="search_div">

      <label htmlFor="armsearch" className='searchlbl'>поиск</label>
      <input type="text" id="armsearch"
        value={input}
        onChange={e => {
          setInput(e.target.value); 
          btnSearchChange(e.target.value);
          arm_Search();
        }
        }
        
        placeholder="Введите данные по компании" />
      <button className={btnClass.join(' ')} disabled={btnDisabled}  >Найти</button>
      
    
      </div>
  <p  style={{ color: 'white', margin:'50px'}}> {data} </p> 
  </div>
  )
}

export default SEARCHSTRING;
